import './TodoItem.css';

import { BiXCircle } from 'react-icons/bi';

function TodoItem(props) {
    return (
        <li className='li_todoItem'>
            <label className="custom-radio-checkbox">
                <span className={`custom-radio-checkbox__show custom-radio-checkbox__show--radio ${props.completed && "otra"}`}
                    onClick={props.onComplete}
                ></span>
                <span className='text_todoItem'>
                    <p className='text_bold'>{props.text}</p>
                    <p >12:41 PM</p>
                </span>
                <span ><svg stroke="currentColor" fill="currentColor" stroke-width="0" viewBox="0 0 24 24" height="1em" width="1em" xmlns="http://www.w3.org/2000/svg"><path d="M9.172 16.242 12 13.414l2.828 2.828 1.414-1.414L13.414 12l2.828-2.828-1.414-1.414L12 10.586 9.172 7.758 7.758 9.172 10.586 12l-2.828 2.828z"></path><path d="M12 22c5.514 0 10-4.486 10-10S17.514 2 12 2 2 6.486 2 12s4.486 10 10 10zm0-18c4.411 0 8 3.589 8 8s-3.589 8-8 8-8-3.589-8-8 3.589-8 8-8z"></path></svg></span>
                <span style={"margin-left: 200px; font-size: 23px"} onClick={props.onDelete}><BiXCircle /></span>
            </label>
        </li >
    );
}
export { TodoItem };
