import React, { Component } from 'react';
import './TodoCounter.css';

const [finish, setFinish] = React.useState(false);

const functionFinish = () => {
  const cuenta = total - completed;
  if (cuenta <= 0) {
    setFinish(true);
  }
};

function TodoCounter({ total, completed }) {
  return (
    < >
      <div className='divTitulo'>
        <h1>Para Hacer</h1>
        <p className='text_bold'>{total} / {completed}</p>
        <h2 className={`invisible ${finish && "visible"}`} >felicidades</h2>
      </div>

    </>
  );
}

export { TodoCounter };
