import logo from "./platzi.webp";
import { TodoCounter } from "./TodoCounter";
import { TodoSearch } from "./TodoSearch";
import { TodoList } from "./TodoList";
import { DoneList } from "./DoneList";
import { DoneCounter } from "./DoneCounter";
import { TodoItem } from "./TodoItem";
import { CreateTodoButton } from "./CreateTodoButton";
import { DoneItem } from "./DoneItem";
import './App.css'
import React from 'react';


// const deafultTodos = [
//   { text: ' Cortar cebolla', completed: false },
//   { text: ' Cortar tomates', completed: false },
//   { text: ' Cortar papa', completed: false },
//   { text: ' Cortar yuca', completed: false },
//   { text: ' Cortar pimentón', completed: false },
//   { text: ' Cortar ajo', completed: false },
// ];

// localStorage.setItem('AlmacenamientoTodos', deafultTodos);

// localStorage.removeItem('AlmacenamientoTodos');











function App() {

  let parsedTodos = localStorage.getItem('Todos');

  const [searchValue, setSearchValue] = React.useState('');
  console.log('buscan  ' + searchValue);

  const [todos, setTodos] = React.useState(parsedTodos);



  const completedTodos = todos.filter(todo => !!todo.completed).length;

  const searchedTodos = todos.filter(todo => {
    const todoText = todo.text.toLocaleLowerCase();

    const searchText = searchValue.toLocaleLowerCase();
    const aux = todoText.includes(searchText);
    console.log(aux);
    return aux && !todo.completed;
  }
  );

  const listDoneTodos = todos.filter(todo => !!todo.completed);

  const totalTodos = todos.length;

  const completeTodo = (text) => {
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(
      (todo) => todo.text === text
    );
    newTodos[todoIndex].completed = true;
    setTodos(newTodos);
  };

  const unCompleteTodo = (text) => {
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(
      (todo) => todo.text === text
    );
    newTodos[todoIndex].completed = false;
    setTodos(newTodos);
  };

  const deleteTodo = (text) => {
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(
      (todo) => todo.text === text
    );
    newTodos.splice(todoIndex, 1);
    setTodos(newTodos);
  };

  return (
    <div className="App">
      <TodoCounter completed={completedTodos} total={totalTodos} />
      <TodoSearch searchValue={searchValue} setSearchValue={setSearchValue} />

      <TodoList>
        {searchedTodos.map(todo => (
          <TodoItem
            key={todo.text}
            text={todo.text}
            completed={todo.completed}
            onComplete={() => completeTodo(todo.text)}
            onDelete={() => deleteTodo(todo.text)}

          />
        ))}
      </TodoList>



      <DoneCounter completed={completedTodos} />
      <DoneList>
        {listDoneTodos.map(todo => (
          <DoneItem
            key={todo.text}
            text={todo.text}
            completed={todo.completed}
            onComplete={() => unCompleteTodo(todo.text)}
            onDelete={() => deleteTodo(todo.text)}
          />
        ))}
      </DoneList>
      <div className="flotante">
        {<CreateTodoButton />}
      </div>

    </div>
  );
}



export default App;
