import logo from "./platzi.webp";
import { TodoCounter } from "./TodoCounter";
import { TodoSearch } from "./TodoSearch";
import { TodoList } from "./TodoList";
import { DoneCounter, DoneList } from "./DoneCounter";
import { TodoItem } from "./TodoItem";
import { CreateTodoButton } from "./CreateTodoButton";
import './App.css'

const deafultTodos = [
  { text: ' Cortar cebolla', completed: true },
  { text: ' Cortar tomates', completed: true },
  { text: ' Cortar papa', completed: true },
  { text: ' Cortar pipa', completed: false },
  { text: ' Cortar pipa', completed: false },
  { text: ' Cortar pipa', completed: false },
];


function App() {
  return (
    <div className="App">
      <TodoCounter completed={7} total={25} />
      <TodoSearch />

      <TodoList>
        {deafultTodos.map(todo => (
          <TodoItem
            key={todo.text}
            text={todo.text}
            completed={todo.completed}
          />
        ))}
      </TodoList>

      {/* <div className="flotante">

        {<CreateTodoButton />}
      </div> */}

      <DoneCounter completed={7} />
      <TodoList>
        {deafultTodos.map(todo => (
          <DoneItem
            key={todo.text}
            text={todo.text}
            completed={todo.completed}
          />
        ))}
      </TodoList>

    </div>
  );
}



export default App;
